import react, { useState } from "react";

const Queue = () => {
    const [temp, settemp] = useState('');
    const [temp2,settemp2]=useState(-1); 
    const [size, setsize] = useState();
    const [top, settop] = useState(-1);
    const [tail, settail] = useState(-1);
    const [queue, setqueue] = useState([]);
    const inputData = (e) => {
        settemp(e.target.value);
    }
    const Submit = (e) => {
        if (!temp) {
            alert('Enter the Value');
        } else {
            e.preventDefault();
            setsize(temp);
            settemp('');
        }
    }
    const Add = () => {
        if (!size) {
            alert('Enter the value');
        }
        else {
            if (tail <= parseInt(size) - 2) {
                settail(tail+ 1);
                settop(0);
                settemp2(0)
                let val = prompt(`Enter the ${tail + 1} value`);
                setqueue([...queue, val]);
            } else {
                alert('Queue Full');
            }
        }
    }
    const Remove = ()=>{
        if(!size || top===size){
            alert('Enter the value');
        }
        else{
            let temp=queue[top];
            let tempArr=queue.filter((elem)=>{
                return elem!==temp;
            })
            setqueue(tempArr);
            if(temp2<size){
                settemp2(temp2+1);
            }
            else{
                alert('Queue Is Empty');
            }
        }
    }
    return (
        <react.Fragment>
            <h1>This Is Queue Page</h1>
            <label>Enter the size Of the Queue</label>
            <input type="number"
                placeholder="from 1 to 10"
                onChange={inputData}
                value={temp}
            />
            <button onClick={Submit}>Submit</button>
            <button onClick={Add}>EnQueue</button>
            <button onClick={Remove}>Dequeue</button>
            <p>Size={size} Top={temp2} Tail={tail}</p>
            <div className="queueData" style={{ display: 'flex' }}>
                {
                    queue.map((data) => {
                        return (
                            <react.Fragment>
                                <p style={{ paddingLeft: '1em' }}>Data={data}</p>
                            </react.Fragment>
                        );
                    })
                }
            </div>
            <div className="queueData" style={{ display: 'flex' }}>
                {
                    queue.map((data, index) => {
                        return (
                            <react.Fragment>
                                <p style={{ paddingLeft: '1em' }}>Index={index}</p>
                            </react.Fragment>
                        );
                    })
                }
            </div>


        </react.Fragment>
    );
}

export default Queue;
