import react, { useEffect, useState } from "react";
import "../Style/Stack.css";
const Stack = () => {
    const [temp, settemp] = useState();
    const [temp2, settemp2] = useState();
    const [size, setsize] = useState();
    const [top, settop] = useState(-1);
    const [stack, setstack] = useState([]);


    const Size = () => {
        if (!temp) {
            alert('Enter the value');
        } else {
            setsize(temp);
            settemp('');
        }
    }
    const Push = () => {
        if (!size) {
            alert('Enter the value');
        }
        else {
            if (top <= parseInt(size) - 2) {
                settop(top + 1);
                let val = prompt(`Enter the ${top + 1} value`);
                setstack([...stack, val]);
            } else {
                alert('Stack Full');
            }
        }
    }

    const Pop = () => {
        if (top === -1) {
            alert('stack empty');
        }
        else {
            let temp = stack[top];
            console.log(temp);
            let tempArr = stack.filter((elem) => {
                return elem !== temp;
            })
            setstack(tempArr);
            settop(top - 1);
        }
    }
    useEffect(() => {
        settemp2(stack[top]);
    }, [Pop]);
    return (
        <react.Fragment>
            <div className="Array">
                <div className="ArrayComp">
                    <div className="Heading">
                        <h1>Stack</h1>
                    </div>
                    <div className="Content">
                        <p><q>A stack is an  array or list structure of function calls and parameters used in modern
                            computer and CPU architecture. Similar to a stack of plates at buffet resturent or cafeteria, element
                            in a stack are added or removed from the top of the stack,in a "last in first , first out" or LIFO order</q></p>
                    </div>
                    <div className="Syntax Common">
                        <div className="input">
                            <label>Enter Stack Value</label>
                            <input type="number"
                                placeholder="Size Between 1 to 10"
                                onChange={(e) => { settemp(e.target.value); }}
                                value={temp || ''}
                            />
                            <button onClick={Size}>submit</button>
                        </div>

                        <div className="function Common">
                            <button onClick={Push}>Push</button>
                            <button onClick={Pop}>POP</button>
                            <button onClick={() => { alert(`Your Top Value Is ${stack[top]}`) }}>Peek</button>
                        </div>
                       <div className="Discription">
                       <p>Size={size} Top={top}</p>
                       </div>
                    </div>
                    
                    <div style={{
                        backgroundColor:"aliceblue",
                        margin: '10em', display: 'flex', flexDirection: 'column-reverse', justifyContent: 'center',
                        alignItems: 'center', borderBottom: '1em solid black', borderRight: '1em solid black',
                        borderLeft: '1em solid black', width: '13%'
                    }}>
                        {
                            stack.map((data, index) => {
                                return (
                                    <react.Fragment>

                                        <p key={index}>Index={index}) Data={data}</p>

                                    </react.Fragment>
                                )
                            })
                        }
                    </div>
                </div>
            </div>
        </react.Fragment>
    );
}

export default Stack;
