import react, { useState } from "react";
import Pointer from "../Images/pointer.png";
import Pointer2 from "../Images/pointer 2.png";
const SinglyLinkList = () => {
    const [Data, setData] = useState([]);
    const [Disp, setDisp] = useState("none");
    const [Flag, setFlag] = useState(0);
    const [top, setTop] = useState(0);

    const AddData = () => {
        if (Flag < 9) {
            setDisp("flex");
            let tempData = prompt(`Enter the Data ${Flag}`);
            let address = { id: new Date().getTime().toString().slice(-4), name: tempData };
            if (tempData) {
                setData([...Data, address]);
                setFlag(Flag + 1);
                setTop(top + 1);
            } else {
                alert('Enter the Data');
            }
        } else {
            alert("OOPS... Memory Full!");
        }
    }
    
    const InsertFirst = () => {
        if (Flag < 9) {
            let tempData = prompt(`Enter the Data ${Flag}`);
            let address = { id: new Date().getTime().toString().slice(-4), name: tempData };
            Data.unshift(tempData);
            if (tempData) {
                setData([address, ...Data]);
                setFlag(Flag + 1);
                setTop(top + 1);
                console.log(Data);
                Data.pop();
               
            } else {
                alert('Enter the Data');
            }
        }
        else{
            alert("OOPS... Memory Full!");
        }
    }
    return (
        <react.Fragment>
            <h1>Link list</h1>
            <div style={{ display: 'flex', justifyContent: 'center', alignItems: 'center' }}>
                <div style={{
                    display: 'flex', flexWrap: 'wrap', border: '4em solid black',
                    width: '100%', borderRadius: '10em', padding: '4em'
                }}>
                    <div style={{ display: Disp }} className="node">
                        {
                            Data.map((data,index) => {
                                return (
                                    <react.Fragment>
                                        <div style={{ width: "50%", border: "1em solid black", backgroundColor: 'lime' }} className="first-half">
                                            <p>Data {index}</p>
                                            <p>{data.name}</p>
                                        </div>
                                        <div style={{ width: "50%", border: "1em solid black", backgroundColor: 'lime' }} className="second-half">
                                            <p>Next</p>
                                            <p>{data.id}</p>
                                        </div>
                                        <img style={{ width: "90em", display: Disp }} src={Pointer2} alt="pointer2" />
                                    </react.Fragment>
                                );
                            })
                        }

                    </div>
                    <div style={{ width: "5%", backgroundColor: "red", display: Disp, justifyContent: "center", alignItems: "center" }} className="null">
                        <p>Null</p>
                    </div>
                    <div style={{ width: '100%', marginTop: '13em' }} className="start">
                        <img style={{ width: '100em', transform: 'rotate(270deg)', marginLeft: '7em', display: Disp }} src={Pointer} alt="pointer" />
                        <p style={{ backgroundColor: 'red', width: 'fit-content', padding: '1em', marginTop: '0.6em', borderRadius: '0.5em', display: Disp }}>Start</p>
                    </div>
                </div>
            </div>
            <button onClick={AddData}>Add</button>
            <button onClick={InsertFirst} style={{ display: Disp }}>Insert First</button>
            <button onClick={()=>{console.log(Data.length," ",Data)}}>Show</button>

        </react.Fragment>
    );
}

export default SinglyLinkList;
