import react , { useState } from "react";
import LinearBinarySearch from "../DataBase/Searching DataBase/LinearBinarySearch";
const LinearSearching = ()=>{
    const [temp , settemp] = useState();//for array
    const [temp2,settemp2]=useState();// for flag
    const [size,setsize]=useState();//for actual array 
    const [flag,setflag]=useState();//for actual flag
    const [index_pos, setIndex_pos]=useState(-1);
    const [disp,setdisp]=useState({
        display:'none',
        bgcolor:'aliceblue',
        color:'black'
    });
    const [Arr,setArr]=useState([]);
    let arr= [];
    const Size = (e)=>{
        e.preventDefault();
       if(temp=== ''){
        alert('enter the value');
       }else{
        setsize(temp);
        settemp('');
        Array([]);
        setArr(arr);
       }
    }
    const Flag = (e)=>{
        e.preventDefault();
       if(temp2=== ''){
        alert('enter the value');
       }
       else{
        setflag(temp2);
        settemp2('');
        setdisp({display:'block'});
       }
    }
    const Array = ()=>{
        for (let index = 0; index < size; index++) {
            let element =Math.floor(Math.random(100)*20);
            arr[index]=element;
        }
    }
    const LinearSearch = async (delay=300)=>{
        setdisp({
            bgcolor:'black',
            color:'aliceblue'
        })
       for (let index = 0; index < size; index++) {
            let ids= document.getElementById(`key${index}`);
            if (parseInt(flag)===Arr[index]) {
                ids.style.backgroundColor="green";
                setIndex_pos(index);
                break;
            }else{
                ids.style.backgroundColor="red";
            }
            await new Promise((resolve)=>setTimeout(()=>{
                resolve();
            },300))
        }
    }
    const Reset = ()=>{
        setIndex_pos(-1);
        for (let index = 0; index < size; index++) {
            let ids= document.getElementById(`key${index}`);
            ids.style.backgroundColor="transparent";
        }

    }
    return(
        <react.Fragment>
            <div className="LinearSearch">
                <h1>{LinearBinarySearch.heading1} Search</h1>
                <p>{LinearBinarySearch.content}</p>
                <div className="Display">
                    <h2>Array Size</h2>
                    <label>Enter the Size</label>
                    <input type="number" 
                    placeholder="0 to 10"
                    onChange={(e)=>{settemp(e.target.value)}}
                    value={temp}
                    />
                    <button onClick={Size}>Submit</button>
                    <label>Enter the Key To Find</label>
                    <input type="number" 
                    placeholder="0 to 10"
                    onChange={(e)=>{settemp2(e.target.value)}}
                    value={temp2}
                    />

                    <button onClick={Flag}>Submit</button>
                    <p>Size Of Array Is={size} And Value to Find Is={flag}</p>
                      <button style={{display:disp.display,backgroundColor:disp.bgcolor,color:disp.color}} onClick={LinearSearch}>Find</button><button style={{display:disp.display}} onClick={Reset}>Reset</button>
                    <div className="screen">
                        {
                            Arr.map((data,index)=>{
                                return(
                                    <react.Fragment>
                                        <div className="box">
                                            <p id={`key${index}`} key={index}>{data}</p>
                                        </div>
                                    </react.Fragment>
                                );
                            })
                        }
                        <p style={{display:disp.display}}>Data Found At INDEX {index_pos}</p>
                    </div>
                </div>
            </div>
        </react.Fragment>
    );
}

export default LinearSearching;
